import React,{useReducer} from "react";
import axios from 'axios';
import ContactContext from './ContactContext';
import ContactReducer from './ContactReducer';
import {ADD_CONTACT,DELETE_CONTACT,SET_CURRENT,CLEAR_CURRENT,UPDATE_CONTACT,FILTER_CONTACTS,CLEAR_FILTER,CONTACT_ERROR,GET_CONTACTS,CLEAR_CONTACTS} from '../types';

const ContactState = props =>{
    const initialState = {
        contacts:[],
        current : null,
        filtered : null,
        error:null
    };
    const [state,dispatch] = useReducer(ContactReducer , initialState);

    //Add contact

    const addContact = async contact =>{
        const config = {
            headers:{
                'Content-Type':'application/json'
            }
        };

        try {
            const res = await axios.post('/api/contacts',contact,config);
            dispatch({type:ADD_CONTACT , payload:res.data});

        }catch (e) {
            dispatch({type:CONTACT_ERROR,payload:e.response.msg});

        }
    };


    //Delete Contact

    const deleteContact = id =>{
        dispatch({type:DELETE_CONTACT , payload:id});
    };

    //Set Current Contact
    const setCurrent = contact =>{
        dispatch({type:SET_CURRENT , payload:contact});
    };

    //clear Current contact
    const clearCurrent = () =>{
        dispatch({type:CLEAR_CURRENT });
    };

    //Update Contact
    const updateContact = contact =>{
        dispatch({type:UPDATE_CONTACT , payload:contact});
    };

    //Filter Contact
    const filterContact = text =>{
        dispatch({type:FILTER_CONTACTS , payload:text});
    };

    //Clear Filter

    const clearFilter = () =>{
        dispatch({type:CLEAR_FILTER });
    };

    return (
        <ContactContext.Provider value={{
            contacts:state.contacts,
            current:state.current,
            filtered : state.filtered,
            error:state.error,
            addContact,
            deleteContact,setCurrent,clearCurrent,updateContact,filterContact,clearFilter
        }}>
            {props.children}

        </ContactContext.Provider>
    );

};

export default ContactState;
