import React, {useContext, useState} from "react";
import AlertContext from '../../context/alert/AlertContext';
import AuthContext from '../../context/auth/AuthContext';




const Register =()=>{
    const alertContext = useContext(AlertContext);
    const authContext = useContext(AuthContext);

    const {setAlert} = alertContext;

    const {register} = authContext;

    const [user,setUser] = useState({
        name:'',
        email:'',
        password:'',
        password2:''
    }) ;

    const {name,email,password,password2}= user;

    const onChange = e => setUser({...user , [e.target.name]:e.target.value});

    const onSubmit = e =>{
        e.preventDefault();
        if(name === '' || email==='' || password ===''){
            setAlert('Please enter all fields','danger');
        }
        else if(password !== password2){
            setAlert('Passwords do not match','danger');
        }
        else {
            register({
                name ,
                email ,
                password
            });
        }
    };


    return(
        <div className='form-container'>
            <h1>
                Account <span className="text-primary">Register</span>
            </h1>

            <form onSubmit={onSubmit}>
                <div className="form-group">
                    <label htmlFor="name">Name</label>
                    <input type="text" onChange={onChange} value={name} name="name" required/>
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email Address</label>
                    <input type="email" onChange={onChange} value={email} name="email" required/>
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input type="password" onChange={onChange} value={password} name="password" minLength="6" required/>
                </div>
                <div className="form-group">
                    <label htmlFor="password2">Confirm Password</label>
                    <input type="password" onChange={onChange} value={password2} name="password2" minLength="6" required/>
                </div>

                <input type="submit" value="Register" className="btn btn-primary btn-block"/>

            </form>

        </div>
    );



};


export default Register;
